import { Component, OnInit } from '@angular/core';
import { Course } from '../types/course';
import { ApiService } from '../api.service';
import { UserService } from '../user/user.service';

@Component({
  selector: 'app-courses-list',
  templateUrl: './courses-list.component.html',
  styleUrls: ['./courses-list.component.css']
})
export class CoursesListComponent implements OnInit {
  courses: Course[] | null = [];
  isLoading: boolean = true;

  constructor(private api: ApiService, private userService: UserService) {}

  get isLoggedIn(): boolean {
    return this.userService.isLogged;
  }

  get userId(): string {
    return this.userService.user?.id || '';
  }

  ngOnInit(): void {
    this.api.getCourses().subscribe((courses) => {
      // TODO: not recommended to do it on front end!
      const sortDatesCB = (
        a: { created_at: string },
        b: { created_at: string }
      ) => (new Date(b.created_at) as any) - (new Date(a.created_at) as any);
      const tempCourses = courses.sort(sortDatesCB as any).slice(0, 5);

      this.courses = tempCourses;

      setTimeout(() => {
        this.isLoading = false;
      }, 1000);
    });
  }


}
<main>
    <app-welcom-msg *ngIf="!isLoggedIn" [isLoggedIn]="isLoggedIn" />
    <div *ngIf="isLoading">
      <app-loader />
    </div>

    <div *ngIf="!isLoading" class="theme-title">
      <!-- theme component  -->
      <div *ngFor="let course of courses" class="theme-container">
        <div class="theme-name-wrapper">
          <div class="theme-name">
            <a [routerLink]="course_id" class="normal">
              <h2>{{ course.courseName | slice : 25 }}</h2>
            </a>
            <div class="columns">
              <div>
                <p>
                  Date: <time>{{ theme.created_at }}</time>
                </p>
                <div class="nick-name">
                  <p>
                    Username: <span>{{ theme.userId.username }}</span>
                  </p>
                </div>
              </div>

            </div>
          </div>
        </div>
      </div>
    </div>
  </main>
