import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { UserService } from '../user.service';
import { Router } from '@angular/router';
import { ProfileDetails } from 'src/app/types/user';

@Component({
  selector: 'app-profil',
  templateUrl: './profil.component.html',
  styleUrls: ['./profil.component.css']
})
export class ProfilComponent implements OnInit{

  showEditMode: boolean = false;

  profileDetails: ProfileDetails = {
    username: '',
    email: '',
  };

  constructor(private userService: UserService, private router: Router, private form: NgForm) {}

  ngOnInit(): void {
    const { username, email } = this.userService.user!;

    this.profileDetails = {
      username,
      email,
    };

    this.form.setValue({
      username,
      email,
    });
  }

  onToggle(): void {
    this.showEditMode = !this.showEditMode;
  }

  saveProfileHandle(): void {
    if (this.form.invalid) {
      return;
    }

    this.profileDetails = this.form.value as ProfileDetails;
    const { username, email, tel } = this.profileDetails;

    this.userService.updateProfile(username, email, tel).subscribe(() => {
      this.onToggle();
    });
  }

  onCancel(e: Event) {
    e.preventDefault();
    this.onToggle();
  }


profil(form: NgForm) {
  if (form.invalid) {
    return;
  }

  const { email, password } = form.value;

  this.userService.login(email, password).subscribe(() => {
    this.router.navigate(['/home']);
  });

}


}

<form  class = "profil" #form="ngForm" (ngSubmit)="profil(form)">


    <div class="container">
      <h3>User profilе</h3>
      <p>You can change your profile here.</p>
      <hr>
      <div>
      <img src="../../../assets/img_avatar.png" alt="Avatar"> 
    </div>
    <ng-container *ngIf="!showEditMode">
        <div class="flex">
          <p>Username:</p>
          <p>{{ profileDetails.username }}</p>
        </div>
        <div class="flex">
          <p>Email:</p>
          <p>{{ profileDetails.email }}</p>
        </div>
        <div class="flex">
          <p>Phone:</p>
          <p>+359 {{ profileDetails.tel }}</p>
        </div>
        <button class="edit-button" (click)="onToggle()">Edit</button>
      </ng-container>

      <label for="email"><b>Email</b></label>
      <input type="text" placeholder="Enter email" name="email" ngModel #inputEmail="ngModel"required>

      <label for="password"><b>Password</b></label>
      <input type="password" placeholder="Enter Password" name="password" ngModel #inputPassword="ngModel" required>

      <button>Login</button>

    </div>
    <div class="container register">
        <p>You are not registered? <a routerLink="/register">Register</a></p>
      </div>

  </form>
