import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { UserService } from '../user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  constructor(private userService: UserService, private router: Router) {}
  register(form: NgForm) {
    if (form.invalid) {
      return;
    }
//debugger
    const { username, email, password, rePassword } = form.value;

    this.userService.register(username, email, password, rePassword);
    this.router.navigate(['/home']);
    ;

  }
}

<form class = "register" #form="ngForm" (ngSubmit)="register(form)">
  <div class="container">
      <h3>Registration form</h3>
      <p>Please fill in this form to create an account.</p>
      <hr>

      <label for="uname"><b>Username</b></label>
      <input type="text" placeholder="Enter Username" name="username" ngModel #inputUsername="ngModel" required>

      <label for="email"><b>Email</b></label>
      <input type="email" placeholder="Enter Email" name="email" ngModel #inputEmail="ngModel" id="email" required>

      <label for="password"><b>Password</b></label>
      <input type="password" placeholder="Enter Password" name="password" id="password" ngModel #inputPassword="ngModel" required>

      <label for="rePassword"><b>Repeat Password</b></label>
      <input type="password" placeholder="Repeat Password" name="rePassword" id="rePassword" ngModel #inputrePassword="ngModel"required>
      <hr>


      <button class="registerbtn">Register</button>
    </div>

    <div class="container signin">
      <p>Already have an account? <a routerLink="/login">Login</a></p>
    </div>
  </form>
