import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { ApiService } from 'src/app/api.service';


@Component({
  selector: 'app-add-course-category',
  templateUrl: './add-course-category.component.html',
  styleUrls: ['./add-course-category.component.css']
})
export class AddCourseCategoryComponent {
  constructor(private apiService: ApiService, private router: Router) {}

  addCourse(form: NgForm) {
    if (form.invalid) {
      return;
    }

    const { courseName, post } = form.value;
    this.apiService.createPost(courseName, post).subscribe(() => {
      this.router.navigate(['/courses']);
    });
  }
}
<form  class = "add-courses" #form="ngForm" (ngSubmit)="addCourse(form)">


    <div class="container">
      <h3>Add new course category</h3>
      <p>Please fill the form below.</p>
      <hr>

      <label for="courseName"><b>Course name</b></label>
      <input type="text" placeholder="Course name category" name="courseName" ngModel #inputCourseName="ngModel" required minlength="5">

      <ng-container *ngIf="inputCourseName.touched">
        <p class="error" *ngIf="inputCourseName?.errors?.['required']">
          Course name is required.
        </p>
        <p class="error" *ngIf="inputCourseName?.errors?.['minlength']">
          Course name must be at least 5 characters long.
        </p>
      </ng-container>

      <label for="post"><b>Post</b></label>
      <textarea type="text" placeholder="Enter your comment here" name="post" id="post" ngModel #textareaPost="ngModel" required minlength="10"></textarea>

      <ng-container *ngIf="textareaPost.touched">
        <p class="error" *ngIf="textareaPost?.errors?.['required']">
          The field with your post is required.
        </p>
        <p class="error" *ngIf="textareaPost.errors?.['minlength']">
          Post must be at least 10 characters long.
        </p>
      </ng-container>
      <button>Cancel</button>
      <button>Post</button>

    </div>
    <div class="container register">
        <p>You are not registered? <a routerLink="/register">Register</a></p>
      </div>

  </form>
