import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { UserService } from '../user.service';
import { Router } from '@angular/router';
import { ProfileDetails } from 'src/app/types/user';

@Component({
  selector: 'app-profil',
  templateUrl: './profil.component.html',
  styleUrls: ['./profil.component.css']
})
export class ProfilComponent implements OnInit{

  showEditMode: boolean = false;

  profileDetails: ProfileDetails = {
    username: '',
    email: '',
  };

  constructor(private userService: UserService, private router: Router, private form: NgForm) {}

  ngOnInit(): void {
    const { username, email } = this.userService.user!;
debugger
    this.profileDetails = {
      username,
      email,
    };

    this.form.setValue({
      username,
      email,
    });
  }

  onToggle(): void {
    this.showEditMode = !this.showEditMode;
  }

  saveProfileHandle(): void {
    if (this.form.invalid) {
      return;
    }

    this.profileDetails = this.form.value as ProfileDetails;
    const { username, email } = this.profileDetails;

    this.userService.updateProfile(username, email).subscribe(() => {
      this.onToggle();
    });
  }

  onCancel(e: Event) {
    e.preventDefault();
    this.onToggle();
  }


profil(form: NgForm) {
  if (form.invalid) {
    return;
  }

  const { email, password } = form.value;

  this.userService.login(email, password).subscribe(() => {
    this.router.navigate(['/home']);
  });

}


}




    <div class="container">
      <h3>My profilе</h3>
      <p>You can change your profile here.</p>
      <hr>
      <div>
      <img src="../../../assets/img_avatar.png" alt="Avatar"> 
    </div>
    <ng-container *ngIf="!showEditMode">
        <div class="flex">
          <p>Username: {{ profileDetails.username }}</p>

        </div>
        <div class="flex">
          <p>Email: {{ profileDetails.email }}</p>

        </div>

        <button class="edit-button" (click)="onToggle()">Edit</button>
      </ng-container>

      <ng-container *ngIf="showEditMode">
        <form [formGroup]="form" (ngSubmit)="saveProfileHandle()">
          <!-- username -->
          <div class="flex">
            <p>Username:</p>
            <input
              type="text"
              name="username"
              id="username"
              [value]="profileDetails.username"
              formControlName="username"
            />
          </div>

          <!-- username validations -->
          <ng-container *ngIf="form.get('username')?.touched">
            <p class="error" *ngIf="form.get('username')?.errors?.['required']">
              Username is required!
            </p>
            <p class="error" *ngIf="form.get('username')?.errors?.['minlength']">
              Username must have at least 5 characters!
            </p>
          </ng-container>

          <!-- email -->
          <div class="flex">
            <p>Email:</p>
            <input
              type="email"
              name="email"
              id="email"
              [value]="profileDetails.email"
              formControlName="email"
            />
          </div>

          <!-- email validations -->
          <ng-container *ngIf="form.get('email')?.touched">
            <p class="error" *ngIf="form.get('email')?.errors?.['required']">
              Email is required!
            </p>
            <p class="error" *ngIf="inputEmail.errors?.['emailValidator']">
              Email is not valid!
            </p>
          </ng-container>


          <!-- Actions -->
          <button class="red-button" (click)="onCancel($event)">Cancel</button>
          <button
            class="green-button"
            [disabled]="form.invalid"
            [style]="{ backgroundColor: form.invalid ? 'grey' : 'green' }"
          >
            Save
          </button>
        </form>
      </ng-container>
