import { Component } from '@angular/core';
import { UserService } from '../user.service';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private userService: UserService, private router: Router) {}
  login(form: NgForm) {
    if (form.invalid) {
      return;
    }
//debugger
    const { username, password } = form.value;

    this.userService.login(username, password);
    this.router.navigate(['/home']);
    ;

  }
}
<form  class = "login" #form="ngForm" (ngSubmit)="login(form)">


    <div class="container">
      <h3>Login form</h3>
      <p>Please fill in this form to login.</p>
      <hr>

      <label for="uname"><b>Username</b></label>
      <input type="text" placeholder="Enter Username" name="uname" ngModel #inputUsername="ngModel"required>

      <label for="psw"><b>Password</b></label>
      <input type="password" placeholder="Enter Password" name="password" ngModel #inputrPassword="ngModel" required>

      <button>Login</button>

    </div>
    <div class="container register">
        <p>You are not registered? <a routerLink="/register">Register</a></p>
      </div>

  </form>
