import { Component } from '@angular/core';
import { UserService } from '../user.service';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private userService: UserService, private router: Router) {}
  login(form: NgForm) {
    if (form.invalid) {
      return;
    }

    const { email, password } = form.value;

    this.userService.login(email, password).subscribe(() => {
      this.router.navigate(['/home']);
    });



  }
}
<form  class = "login" #form="ngForm" (ngSubmit)="login(form)">


    <div class="container">
      <h3>Login form</h3>
      <p>Please fill in this form to login.</p>
      <hr>

      <label for="email"><b>Email</b></label>
      <input type="text" placeholder="Enter email" name="email" ngModel #inputEmail="ngModel"required>

      <ng-container *ngIf="inputEmail.touched">
        <p class="error" *ngIf="inputEmail.errors?.['required']">
          Email is required!
        </p>
        <p class="error" *ngIf="inputEmail.errors?.['emailValidator']">
          Email is not valid!
        </p>
      </ng-container>

      <label for="password"><b>Password</b></label>
      <input type="password" placeholder="Enter Password" name="password" ngModel #inputPassword="ngModel" required>

      <button>Login</button>

    </div>
    <div class="container register">
        <p>You are not registered? <a routerLink="/register">Register</a></p>
      </div>

  </form>
