import { Injectable, OnDestroy } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { UserForAuth } from '../types/user';
import { BehaviorSubject, Subscription, tap } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class UserService implements OnDestroy {

  private user$$ = new BehaviorSubject<UserForAuth | undefined>(undefined);
  private user$ = this.user$$.asObservable();

  user: UserForAuth | undefined;
  USER_KEY = 'user';

  userSubscription: Subscription;

  get isLogged(): boolean {
    return !!this.user;
  }
  private apiUrl = 'http://localhost:3000'
  constructor(private http: HttpClient) {this.userSubscription = this.user$.subscribe((user) => {
    this.user = user;
  });
}

  clean(): void {
    window.sessionStorage.clear();
  }



  register(
    username: string,
    email: string,
    password: string,
    rePassword: string
  ) {

    window.sessionStorage.removeItem(this.USER_KEY);
    window.sessionStorage.setItem(this.USER_KEY, JSON.stringify(this.user));

    //const api = `http://localhost:3000`
    return this.http
      .post<UserForAuth>(`${this.apiUrl}/auth/register`, {
        username,
        email,
        password,
        rePassword
      })
      .pipe(tap((user) => this.user$$.next(user)));


  }

  login (username: string, password: string) {
    return this.http
    .post<UserForAuth>(`${this.apiUrl}/register`, {
      username,
      password,

    })
    .pipe(tap((user) => this.user$$.next(user)));
  }
  logout() {
    return this.http
      .post(`${this.apiUrl}/logout`, {})
      .pipe(tap(() => this.user$$.next(undefined)));
  }

  ngOnDestroy(): void {
    this.userSubscription.unsubscribe();
  }
}
